call insere_a_direita_dos_filhos('fundamentacao_indicadores', 'auto_2022-08-25_22:23:11.167', 'Para que os indicadores acima possam ser alcançados é preciso uma boa escolha da estruturação de dados, assunto que será tratado adiante.', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'paragrafo'));
