call insere_a_direita_dos_filhos('caracterizacao_dos_resultados_do_WASH', 'auto_2023-02-01_11:19:18.059', 'participantes por sexo;', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'item_lista_num'));
call insere_a_direita_dos_filhos('caracterizacao_dos_resultados_do_WASH', 'auto_2022-09-09_13:37:44.120', 'número de bolsistas;', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'item_lista_num'));
