call insere_a_direita_dos_filhos('auto_2023-01-26_13:11:19.319', 'auto_2023-01-27_17:22:28.795', 'UNICAMP - CIN (Centro Integrado de Nefrologia) - Campinas/SP, oficializa adesão à metodologia do Programa WASH em 24/05/2022', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'item_lista_num'));
