call insere_a_direita_dos_filhos('apendice_c', 'auto_2022-09-22_15:04:53.741', 'Tabela de cidades num banco de dados relacional.', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'legenda_tabela'));
call insere_a_direita_dos_filhos('apendice_c', 'auto_2022-09-22_15:05:05.355', 'Nome | Data de Nascimento | Identificador da Cidade
José | 10/10/2010 | 1
