call insere_a_direita_dos_filhos('Hipoteses_Introducao', 'item_1_lista_Hipoteses', 'O WASH teve como origem as experiências do Projeto Governo Eletrônico de Serviço de Atendimento ao Cidadão (GESAC), da avaliação do OLPC ([[NEGROPONTE, 2004]]) e da Avaliação do PID ([[MAMMANA, 2009]]);', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'item_lista_num'));
