call insere_a_direita_dos_filhos('auto_2023-02-04_18:20:25.087', 'auto_2023-02-06_14:54:06.053', 'Hipótese 2: O WASH estimula a aprendizagem por meio da orientação a projetos para alunos dos ensinos médio e técnico e graduação em temas relacionados ao STEAM.', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'citacao'));
