call insere_a_direita_dos_filhos('narrativa_do_GESAC', 'auto_2022-11-03_15:42:39.806', 'Rafael Gomes da Cruz (Banto Palmarino) (ver Fig. 16);', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'item_lista_num'));
