call insere_a_direita_dos_filhos('narrativa_do_GESAC', 'auto_2022-11-03_17:18:57.988', 'A presente autora, ao lado de Vincenzo Tozzi, implementador que também veio a contribuir com o WASH.', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'legenda_imagem'));
