call insere_a_direita_dos_filhos('auto_2022-08-15_18:21:58.553', 'auto_2022-11-30_22:13:51.453', 'Vídeo/Entrevista: Papert e Afira Ripper', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'topico'));
