call insere_a_direita_dos_filhos('lixeira', 'auto_2023-02-09_19:04:14.182', 'A modelagem de processos do WASH deve ser integrada ao desenho da Platuóxe, para aumentar a qualidade da coleta de dados, criando mecanismos de responsabilização e premiação para os partícipes, em suas obrigações de entrada de dados;', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'item_lista_num'));
call insere_a_direita_dos_filhos('lixeira', 'auto_2022-09-21_12:32:06.643', 'Na representação, cada linha é o cadastro de um estudante e seus dados estão dispostos em colunas, como segue:', '', (select id_chave_nested_tipo_secao from nested_tipos_secoes where nome_nested_tipo_secao = 'paragrafo'));
