select raiz, tipo, derivado from (select t1.nome_token as raiz, (select nome_tipo_token from tipos_tokens where t1.id_tipo_token=id_chave_tipo_token) as tipo, ifnull(group_concat(concat (t2.nome_token,' (',(select acentuada from tipos_flexoes where t2.id_tipo_flexao=id_chave_tipo_flexao),')') separator ', '), 'NÃO TEM DERIVADOS') as derivado, t1.id_raiz as pai from tokens as t1 left join tokens as t2 on t1.id_chave_token = t2.id_raiz group by t1.nome_token having pai is null) as subquery;

CREATE TABLE frases (
    id_chave_frase INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    nome_frase varchar(200),
    id_tipo_elemento_sintatico int,
    time_stamp TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP(6) ON UPDATE CURRENT_TIMESTAMP(6),
    unique(nome_frase),
    FOREIGN KEY (id_tipo_elemento_sintatico) REFERENCES tipos_elementos_sintaticos(id_chave_tipo_elemento_sintatico)
);

CREATE TABLE tokens_nas_frases (
    id_chave_token_na_frase INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    nome_token_na_frase varchar(200),
    id_frase int,
    id_token int,
    ordem int,
    time_stamp TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP(6) ON UPDATE CURRENT_TIMESTAMP(6),
    unique(id_frase, id_token, ordem),
    FOREIGN KEY (id_frase) REFERENCES frases(id_chave_frase),
    FOREIGN KEY (id_token) REFERENCES tokens(id_chave_token)
);

