DROP TABLE IF EXISTS paises_iso3166_2;

CREATE TABLE paises_iso3166_2 (
    id_chave_pais_iso3166_2 INT AUTO_INCREMENT PRIMARY KEY,
    nome_pais_iso3166_2 VARCHAR(255) NOT NULL UNIQUE,
    codigo_alpha2 CHAR(2) NOT NULL UNIQUE,
    codigo_alpha3 CHAR(3) NOT NULL UNIQUE,
    codigo_numerico CHAR(3) NOT NULL UNIQUE
);





UPDATE paises
INNER JOIN paises_iso3166_2 ON paises.nome_pais = paises_iso3166_2.nome_pais_iso3166_2
SET
    paises.codigo_alpha2 = paises_iso3166_2.codigo_alpha2,
    paises.codigo_alpha3 = paises_iso3166_2.codigo_alpha3,
    paises.codigo_numerico = paises_iso3166_2.codigo_numerico;

UPDATE paises SET codigo_alpha2 = 'GB' WHERE nome_pais = 'United Kingdom';
